import React, { useEffect, useState } from "react";

export default function Balance() {
  const [income, setIncome] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const getIncome = () => {
    const url = "http://localhost:8000/operations/income";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setIncome(data));
  };

  const getExpenses = () => {
    const url = "http://localhost:8000/operations/expenses";

    fetch(url)
      .then((respones) => response.json())
      .then((data) => setExpenses(data));
  };

  useEffect(getIncome, []);
  useEffect(getExpenses, []);

  const totalExpenses = () => {
    let total = 0;
    const totalExpenses = expenses.map((egress) => {
      return (total = +egress.amount);
    });
    return total;
  };

  return console.log(totalExpenses());
}
