import React, { useEffect, useState } from "react";
import Axios from "axios";

import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";

export default function Income() {
  const [categories, setCategories] = useState([]);
  const [income, setIncome] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [concept, setConcept] = useState("");
  const [amount, setAmount] = useState(0);
  const [date, setDate] = useState("");
  const [id_category, setCategory] = useState(1);
  const [titleModal, setTitleModal] = useState("");

  const showModalIncome = () => {
    setShowModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
  };
  const showTitleModal = () => {
    return titleModal;
  };
  const deleteEntry = (idDelete) => {
    Axios.delete(`http://localhost:8000/operations/${idDelete}`);
  };
  const updateEntry = (ops) => {
    Axios.put(`http://localhost:8000/operations/${ops.id}`, {
      concept: ops.concept,
      amount: ops.amount,
      date: ops.date,
      id_category: ops.id_category,
    });
  };

  const submitEntry = () => {
    Axios.post("http://localhost:8000/operations/income", {
      id_user: 2,
      concept: concept,
      amount: amount,
      date: date,
      id_category: id_category,
    });
    setIncome([
      ...income,
      {
        id_user: 2,
        concept: concept,
        amount: amount,
        date: date,
        id_category: id_category,
      },
    ]);
    closeModal();
  };

  const getCategories = () => {
    Axios.get("http://localhost:8000/categories").then((response) => {
      setCategories(response.data);
    });
  };

  const getIncome = () => {
    Axios.get("http://localhost:8000/operations/income").then((response) => {
      setIncome(response.data);
    });
  };

  useEffect(getIncome, []);
  useEffect(getCategories, []);

  const getRows = () => {
    const rows = income.map((entry) => {
      return (
        <tr>
          <td>{entry.id}</td>
          <td>{entry.concept}</td>
          <td>${entry.amount}</td>
          <td>{entry.date}</td>
          <td>{entry.id_category}</td>
          <td>
            <Button
              onClick={() => {
                showModalIncome();
                updateEntry(entry);
                setTitleModal("Edit entry");
              }}
              variant="secondary"
              className="mx-1"
            >
              Edit
            </Button>
            <Button
              onClick={() => {
                deleteEntry(entry.id);
              }}
              variant="danger"
              className="mx-1"
            >
              Remove
            </Button>
          </td>
        </tr>
      );
    });
    return rows;
  };

  const showCategories = () => {
    const options = categories.map((category) => {
      return (
        <option>
          {category.id} - {category.name}
        </option>
      );
    });
    return options;
  };

  return (
    <>
      <Container>
        <Row>
          <Col>
            <Button
              onClick={() => {
                showModalIncome();
                setTitleModal("New entry");
              }}
              variant="primary"
              className="my-3 mx-1"
            >
              New
            </Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Concept</th>
                  <th>Amount</th>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Options</th>
                </tr>
              </thead>
              <tbody>{getRows()}</tbody>
            </table>
          </Col>
        </Row>
        <Modal show={showModal} onHide={closeModal}>
          <Modal.Header closeButton>
            <Modal.Title>{showTitleModal()}</Modal.Title>
          </Modal.Header>

          <Form className="mx-3 my-3">
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Concept</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter concept"
                onChange={(e) => {
                  setConcept(e.target.value);
                }}
                defaultValue="fasf"
              />
              <Form.Label>Amount</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter amount"
                onChange={(e) => {
                  setAmount(e.target.value);
                }}
                defaultValue="fasf"
              />
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              >
                {" "}
                {showCategories()}
              </Form.Control>
            </Form.Group>
          </Form>

          <Modal.Footer>
            <Button onClick={closeModal} variant="secondary">
              Cancel
            </Button>
            <Button variant="primary" onClick={submitEntry}>
              Save
            </Button>
          </Modal.Footer>
        </Modal>
      </Container>
    </>
  );
}
