import React, { useEffect, useState } from "react";

import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";

export default function Income() {
  const [categories, setCategories] = useState([]);
  const [income, setIncome] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [entrySelected, setEntry] = useState(0);
  const [concept, setConcept] = useState("");
  const [amount, setAmount] = useState(0);
  const [date, setDate] = useState("");
  const [category, setCategory] = useState("");

  const showModalIncome = () => {
    setShowModal(true);

    if (entrySelected > 0) {
      const url = `http://localhost:8000/operations/${entrySelected}`;

      fetch(url)
        .then((response) => response.json())
        .then((data) => {
          setConcept(data.concept);
          setAmount(data.amount);
          setDate(data.date);
          setCategory(data.id_category);
        });
    } else {
      setConcept("");
      setAmount("");
      setDate("");
      setCategory("");
    }
  };
  const closeModal = () => {
    setShowModal(false);
  };

  const getCategories = () => {
    const url = "http://localhost:8000/categories";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setCategories(data));
  };

  const getIncome = () => {
    const url = "http://localhost:8000/operations/income";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setIncome(data));
  };

  useEffect(getIncome, []);
  useEffect(getCategories, []);

  const getRows = () => {
    const rows = income.map((entry) => {
      return (
        <tr>
          <td>{entry.id}</td>
          <td>{entry.concept}</td>
          <td>${entry.amount}</td>
          <td>{entry.date}</td>
          <td>{entry.id_category}</td>
          <td>
            <Button
              onClick={setEntry(entry.id)}
              variant="secondary"
              className="mx-1"
            >
              Edit
            </Button>
            <Button
              onClick={setEntry(entry.id)}
              variant="danger"
              className="mx-1"
            >
              Remove
            </Button>
          </td>
        </tr>
      );
    });
    return rows;
  };

  const showCategories = () => {
    const options = categories.map((category) => {
      return <option>{category.name}</option>;
    });
    return options;
  };

  return (
    <>
      <Container>
        <Row>
          <Col>
            <Button
              onClick={showModalIncome}
              variant="primary"
              className="my-3 mx-1"
            >
              New
            </Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Concept</th>
                  <th>Amount</th>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Options</th>
                </tr>
              </thead>
              <tbody>{getRows()}</tbody>
            </table>
          </Col>
        </Row>
        <Modal show={showModal} onHide={closeModal}>
          <Modal.Header closeButton>
            <Modal.Title>New entry</Modal.Title>
          </Modal.Header>

          {
            //FORM
          }
          <Form className="mx-3 my-3">
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>{concept}</Form.Label>
              <Form.Control type="text" placeholder="Enter concept" />
              <Form.Label>{amount}</Form.Label>
              <Form.Control type="number" placeholder="Enter amount" />
              <Form.Label>{date}</Form.Label>
              <Form.Control type="date" />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>{category}</Form.Label>
              <Form.Control as="select"> {showCategories()}</Form.Control>
            </Form.Group>
          </Form>
          {
            //TERMINA FORM
          }

          <Modal.Footer>
            <Button onClick={closeModal} variant="secondary">
              Cancel
            </Button>
            <Button variant="primary">Save</Button>
          </Modal.Footer>
        </Modal>
      </Container>
    </>
  );
}
