import React, { useEffect, useState } from "react";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

export default function Home() {
  const [operations, setOperations] = useState([]);
  const [income, setIncome] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const getIncome = () => {
    const url = "http://localhost:8000/operations/income";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setIncome(data));
  };
  const getExpenses = () => {
    const url = "http://localhost:8000/operations/expenses";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setExpenses(data));
  };

  const getOperations = () => {
    const url = "http://localhost:8000/operations";

    fetch(url)
      .then((response) => response.json())
      .then((data) => setOperations(data));
  };

  useEffect(getOperations, []);
  useEffect(getIncome, []);
  useEffect(getExpenses, []);

  const getTotal = () => {
    let total = [];
    let totalExpenses = 0;
    let totalIncome = 0;

    for (let i = 0; i < expenses.length; i++) {
      totalExpenses += expenses[i].amount;
    }
    for (let i = 0; i < income.length; i++) {
      totalIncome += income[i].amount;
    }

    total.push(totalIncome - totalExpenses);

    return total;
  };

  const getRows = () => {
    const rows = operations.map((operation) => {
      return (
        <tr>
          <td>{operation.id}</td>
          <td>{operation.concept}</td>
          <td>${operation.amount}</td>
          <td>{operation.date}</td>
          <td>{operation.id_category}</td>
          <td>{operation.type}</td>
          <td>
            <Button variant="secondary" className="mx-1">
              Edit
            </Button>
            <Button variant="danger" className="mx-1">
              Remove
            </Button>
          </td>
        </tr>
      );
    });

    return rows;
  };

  return (
    <>
      <Container fluid>
        <Row>
          <Col>
            <Card style={{ width: "18rem" }} className="mx-auto my-auto">
              <Card.Title className="mx-auto my-auto">{getTotal()}</Card.Title>
              <Card.Body>
                <Card.Title className="mx-auto my-auto">
                  Current Balance
                </Card.Title>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col>
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Concept</th>
                  <th>Amount</th>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Type</th>
                  <th>Options</th>
                </tr>
              </thead>
              <tbody>{getRows()}</tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </>
  );
}
